/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  --color-tint: #f8fbff;

  --font-heading: "Space Grotesk", sans-serif;
  --font-body: "Open Sans", sans-serif;

  /* Colors */
  --color-base: #183b56;
  --color-brand: #1565d8;
  --color-accent: #36b37e;
  --color-accent-2: #0d2436;
  --color-light: #fcfcfd;
  --color-shade: #cbcace;
  --color-inverted: white;
  --color-tint: #e5eaf4;

  /* Base values */
  --color: var(--color-base);
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #eee;
  --background: white;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-body);
  color: var(--color-base);
  line-height: 1.2;
  font-size: 1.125rem;
  background: var(--background);
}

.section.has-content {
  display: flex;
  justify-content: center;
  padding: 5rem 2rem;

  .content {
    max-width: 800px;
    width: 100%;
  }
}

.section-container {
  max-width: 1250px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

.heading-group {
  display: grid;
  gap: 1rem;
  place-content: center;
  text-align: center;

  .superhead {
    font-family: var(--font-body);
    color: var(--color-accent);
    font-size: 0.875rem;
    font-weight: 500;
    letter-spacing: 1.5px;
    text-transform: uppercase;
  }

  .subheading {
    color: #4f6373;
    line-height: 1.4;
    max-width: 600px;
    font-weight: 400;
    max-width: 600px;
    margin: 0 auto;
  }
}

.heading {
  font-family: var(--font-heading);
  font-size: 2rem;
  line-height: 1.1;
  font-weight: 500;
  max-width: 600px;
}

.button {
  color: var(--color-brand, white);
  background: var(--color-inverted);
  border: 2px solid var(--color-brand);
  border-radius: 6px;
  padding: 8px 20px;
  transition: 0.1s background, 0.1s color;

  &:hover {
    color: var(--color-inverted);
    background: var(--color-brand);
    border-color: var(--color-inverted);
  }

  &.inverted {
    background: var(--color-white);
    color: var(--color-brand);
    border-color: #0d2436;
  }
}

.link {
  font-size: 1.125rem;
  font-weight: 400;
  color: var(--color-brand);

  .arrow {
    transition: transform 0.1s;
  }

  &:hover .arrow {
    transform: translateX(4px);
  }
}

.section-container {
  display: grid;
  gap: 4rem;
}

ul {
  display: grid;
  place-content: center;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 4rem;

  li {
    display: grid;
    place-content: start;
    gap: 2rem;

    .quote {
      font-size: 1.125rem;
      line-height: 1.75;
    }

    .person {
      display: flex;
      gap: 1rem;

      img {
        width: 40px;
        height: 40px;
        object-fit: cover;
        border-radius: 50%;
      }

      .text {
        display: grid;
        gap: 0.25rem;
      }

      .name {
        font-weight: 600;
      }

      .subtitle {
        font-size: 0.875rem;
      }
    }
  }
}

.section-container {
  padding-block: 5rem;
}

.section-container {
  /* Defined in Site CSS  */
  max-width: 700px;
  width: 100%;
  margin: 0 auto;
  gap: 1rem;
}

.box{
  padding: 3rem 3.25rem;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius, 4px);
}
.heading-group {
  display: grid;
  gap: 1rem;
  margin-bottom: 2rem;

}

form {
  display: grid;
  gap: 2rem;


  label {
    display: grid;
    gap: 0.25rem;
    font-weight: 500;
  }

  input,
  textarea {
    width: 100%;
    padding: 0.75rem 0.75rem;
    border: 1.5px solid var(--border-color);
    border-radius: 4px;
  }

  .label {
    font-size: 0.85rem;
    margin-bottom: 0.25rem;
  }
  .placeholder {
    font-size: 1rem;
    font-weight: 300;
  }
  .button {
    /*Defined in Site CSS*/
    place-self: start;
  }
}

