.section-container {
  /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.logo {
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 2rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;

      &:hover {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

.heading-group {
  margin-bottom: 6rem;
}

.teasers {
  display: grid;
  gap: 6rem;
}

.teaser {
  display: grid;
  gap: 3rem;

  @media (min-width: 1200px) {
    gap: 6rem;
  }

  img {
    width: 100%;
    border-radius: var(--border-radius);
  }

  .body {
    /* Defined in Site CSS */
    display: grid;
    place-items: flex-start;
    gap: 1rem;
    padding: 0;
    line-height: 1.5;
    max-width: 450px;

    .title {
      font-family: var(--font-heading);
      font-size: 1.75rem;
      font-weight: 500;
      line-height: 1.1;
    }

    a.link {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }
  }

  @media (min-width: 800px) {
    grid-template-columns: 1fr 1fr;
    place-items: center;

    /* switch direction for even items */
    &:nth-child(odd) img {
      grid-row: 1;
      grid-column: 2;
    }

    &:nth-child(even) img {
      grid-row: 1;
      grid-column: 1;
    }
  }
}

.section-container {
  max-width: var(--inner-max-width,800px);
  padding: 4rem 2rem;
}

.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.25rem 0;
    line-height: 1.5;
    font-family: var(--body-font);
    font-weight: 400;
    margin-bottom: 1rem;
    color: #2d3339;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: clamp(1.75rem, 10vw, 2.25rem);
    font-weight: 600;
    letter-spacing: -0.08rem;
    margin-bottom: 1rem;
    line-height: 1.1;
  }

  :global(h2) {
    font-size: clamp(1.5rem, 10vw, 2.125rem);
    font-weight: 600;
    margin-bottom: 1rem;
    letter-spacing: -0.06rem;
    line-height: 1.1;
  }

  :global(h3) {
    font-size: clamp(1.25rem, 10vw, 1.75rem);
    font-weight: 600;
    margin-bottom: 0.5rem;
    letter-spacing: -0.05rem;
    line-height: 1.1;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.25rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding-left: 1.25rem;
    margin-bottom: 2rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container {
